	private void bind(org.jsoup.nodes.Document doc, JsonObject jo, String[] items) {
		int cnt = 0;
		if(items != null) {
			cnt = items.length;
			for(int i=0; i<cnt; i++) {
				//System.err.println(items[i]);
				if(!"Tojibi".equals(items[i]) && !"Jesegeum".equals(items[i])
				   && !"Gongsabi".equals(items[i]) && !"Inheogabi".equals(items[i])
				   && !"Budamgeum".equals(items[i]) && !"Saeobgyeongbi".equals(items[i])
				   && !"Jichool".equals(items[i])   && !"IncomeSell".equals(items[i])
				   && !"IncomeManage".equals(items[i]) && !"Income".equals(items[i])
				   && !"Maechool".equals(items[i]) && !"Gyeongsang".equals(items[i])
				   && !"Mymoney".equals(items[i])) {
					
					if("Jaesanse".equals(items[i])) {
						doc.select("span#tJaesanseT1").first().text(jo.get("tJaesanseT1").getAsString());
						doc.select("span#tJaesanseT2").first().text(jo.get("tJaesanseT2").getAsString());
						doc.select("span#sJaesanseT3").first().text(jo.get("sJaesanseT3").getAsString());
						doc.select("span#wJaesanse").first().text(jo.get("wJaesanse").getAsString() + "원");
						doc.select("span#rJaesanse").first().text(jo.get("rJaesanse").getAsString() + "%");
						
						doc.select("span#tJaesanseH1").first().text(jo.get("tJaesanseH1").getAsString());
						doc.select("span#tJaesanseH2").first().text(jo.get("tJaesanseH2").getAsString());
						doc.select("span#tJaesanseH3").first().text(jo.get("tJaesanseH3").getAsString());
						doc.select("span#cJaesanseH").first().text(jo.get("cJaesanseH").getAsString());
						doc.select("span#wJaesanse2").first().text(jo.get("wJaesanse2").getAsString() + "원");
					}
					else if("FarmBudam".equals(items[i])) {
						
						//지목이 농지면
						if("Y".equals(jo.get("isFarm").getAsString())) {
							if("Y".equals(jo.get("isFarmBudamGammyeon").getAsString())) {
								doc.select("span#tFarmBudam").first().text("부담금 감면");
							}
							else {
								doc.select("span#tFarmBudam").first().text(jo.get("tFarmBudam").getAsString() + " X ");
								doc.select("span#sFarmBudam").first().text(jo.get("sFarmBudam").getAsString());
							}
						}
						else {
							doc.select("span#tFarmBudam").first().text("-");
						}
					}
					else if("AlterSanrim".equals(items[i])) {
						//지목이 임야면
						if("Y".equals(jo.get("isForest").getAsString())) {
							doc.select("span#tAlterSanrim").first().text(jo.get("tAlterSanrim").getAsString() + " X ");
							doc.select("span#sAlterSanrim").first().text(jo.get("sAlterSanrim").getAsString());
						}
						else {
							doc.select("span#tAlterSanrim").first().text("-");
						}
					}
					else if("DaechulIja".equals(items[i])) {
						doc.select("span#tDaechulIja").first().text(jo.get("tDaechulIja").getAsString());
						doc.select("span#sDaechulIja").first().text(jo.get("sDaechulIja").getAsString());
						doc.select("span#oDaechulIja").first().text(jo.get("oDaechulIja").getAsString());
					}
					else {
						doc.select("span#t" + items[i]).first().text(jo.get("t" + items[i]).getAsString());
						doc.select("span#s" + items[i]).first().text(jo.get("s" + items[i]).getAsString());
					}
					
				}
				
				doc.select("span#w" + items[i]).first().text(jo.get("w" + items[i]).getAsString() + "원");
				
				if(!"Mymoney".equals(items[i])) {
					doc.select("span#r" + items[i]).first().text(jo.get("r" + items[i]).getAsString() + "%");
				}
				
			}
		}
	}
	
	
