mapper.select:
	select concat(code, ',"', name, '"') 
	  from (
			select distinct [시군구코드] as code,[시군구명] as name 
			  from [지번주소].[dbo].[PNU10] 
			 where [시도코드] = #{si}
			   and [시군구명] != ''
		   ) t1
      order by name

	select concat(code, ',"', name, '"') 
	  from (
			select distinct [읍면동코드] as code, [읍면동명] as name 
			  from [지번주소].[dbo].[PNU10] 
			 where [시도코드] = #{si}
			   and [시군구코드] = #{gugun}
			   and [읍면동명] != ''
		   ) t1
      order by name


		select concat('"',[PNU], '","', [시도명], ' ', [시군구명], ' ', [읍면동명], ' ', [리명], ' 산', ' ', [본번숫자], '-', [부번숫자], '",',  [위도], ',', [경도], ',', [극북], ',', [극서]) 

   		  from (

   		  		select [PNU],[필지구분코드],[본번숫자],[부번숫자],[시도명],[시군구명],[읍면동명],[리명],[위도],[경도],[극서],[극북]

				  from [지번주소].[dbo].[전국지번통합]  
			     where [시도코드] = #{si}
			       and [시군구코드] = #{gugun}
			       and [읍면동코드] = #{region}

			       and [필지구분코드] = #{san}

			       and [본번숫자]= #{beonjiF}


			       and [부번숫자]= #{beonjiS}


			       and [위도] is not null
			    ) tb1

