mapper.select:
select 고유번호, 물건소재지, PNU, 공고기관, 사업명, 공고일, 공고번호, 사업시행자, 시설종류, 위도, 경도
  from [수작업].[dbo].[보상물건]
 where 고유번호 = #{unu}

select concat(고유번호, '`', 물건소재지) from [수작업].[dbo].[보상물건] where 고유번호 IN
#{item}
order by 물건소재지

{call [데이터수집].[dbo].[SEL_실거래가_범위]( 
	#{swx, mode=IN, jdbcType=DECIMAL, javaType=string},
	#{nex, mode=IN, jdbcType=DECIMAL, javaType=string},
	#{swy, mode=IN, jdbcType=DECIMAL, javaType=string},
	#{ney, mode=IN, jdbcType=DECIMAL, javaType=string}
)}

		 select a.PNU, a.계약년월, a.계약일, a.대지권면적, a.거래금액, a.지목, a.[지번], a.[용도지역], a.[지분구분], a.[도로조건], a.단지명, a.전용면적, a.층, a.건축년도, a.도로명
  		   from (
				select distinct	a.PNU, a.계약년월, a.계약일, a.대지권면적, a.거래금액, b.지목, a.경도, a.위도,
					   c.[FullName] [지번], c.용도명 [용도지역], null [지분구분], null [도로조건], a.단지명, a.전용면적, a.층, a.건축년도, a.도로명
				  from [데이터수집].[dbo].[실거래가_매매_클린징_최종계약년월] a with (nolock) 
				 inner join [서비스].[dbo].[토지기본정보PNU] b with (nolock, index([PK_토지기본정보PNU])) on (b.PNU=a.PNU)
				  left outer join [지번주소].dbo.전국지번통합 c with (nolock) on (c.PNU=a.PNU)
		  		 where a.pnu = #{pnu}
		) a

{call [수작업입력].[dbo].[SEL_건축허가정보]( 
	#{swx, mode=IN, jdbcType=DECIMAL, javaType=string},
	#{nex, mode=IN, jdbcType=DECIMAL, javaType=string},
