mapper.select:
select count(매물번호)
  from [웹].[dbo].[매물등록]
 where 계정아이디 = #{accountId}
   and PNU = #{pnu}

select count(요청번호)
  from [웹].[dbo].[컨설팅요청]
 where 계정아이디 = #{accountId}
   and PNU = #{pnu}

mapper.delete:
delete 
  from [웹].[dbo].[관심물건]
 where 계정아이디 = #{accountId}
   and 관심물건번호 = #{gwansimMulgeonNum}

mapper.update:
update [웹].[dbo].[관심물건]
   set 메모 = #{memo},
   	       분류등급 = #{grade}
 where 계정아이디 = #{accountId}
   and 관심물건번호 = #{gwansimMulgeonNum}

mapper.select:
